        setError(response.message || '–ü—Ä–æ–∏–∑–æ—à–ª–∞ –æ—à–∏–±–∫–∞. –ü–æ–ø—Ä–æ–±—É–π—Ç–µ –ø–æ–∑–∂–µ.');
      } else {
        // Add AI response to chat
        setMessages(prev => [...prev, { 
          role: 'assistant', 
          content: response.message,
          messageId: response.message_id,
          saved: false
        }]);
      }
